describe CrimesController do

  # This should return the minimal set of attributes required to create a valid
  # Crime. As you add validations to Crime, be sure to
  # update the return value of this method accordingly.
  def valid_attributes
    {}
  end

  describe "GET index" do
    it "assigns all crimes as @crimes" do
      crime = Crime.create! valid_attributes
      get :index
      assigns(:crimes).should eq([crime])
    end
  end

  describe "GET show" do
    it "assigns the requested crime as @crime" do
      crime = Crime.create! valid_attributes
      get :show, :id => crime.id.to_s
      assigns(:crime).should eq(crime)
    end
  end

  describe "GET new" do
    it "assigns a new crime as @crime" do
      get :new
      assigns(:crime).should be_a_new(Crime)
    end
  end

  describe "GET edit" do
    it "assigns the requested crime as @crime" do
      crime = Crime.create! valid_attributes
      get :edit, :id => crime.id.to_s
      assigns(:crime).should eq(crime)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "creates a new Crime" do
        expect {
          post :create, :crime => valid_attributes
        }.to change(Crime, :count).by(1)
      end

      it "assigns a newly created crime as @crime" do
        post :create, :crime => valid_attributes
        assigns(:crime).should be_a(Crime)
