        assigns(:crime).should be_a_new(Crime)
      end

      it "re-renders the 'new' template" do
        # Trigger the behavior that occurs when invalid params are submitted
        Crime.any_instance.stub(:save).and_return(false)
        post :create, :crime => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested crime" do
        crime = Crime.create! valid_attributes
        # Assuming there are no other crimes in the database, this
        # specifies that the Crime created on the previous line
        # receives the :update_attributes message with whatever params are
